!function(){const e=$(".add-new-project"),t=$(".modal-container"),n=$(".close-modal"),s=$(".overlay");e.click((function(){t.removeClass("hide"),s.show()})),n.click((function(){t.addClass("hide"),s.hide()}));$("#add-project-form").submit((function(e){e.preventDefault(),$.ajax({type:"POST",url:$(this).attr("action"),data:$(this).serialize(),success:function(e){const{addedProject:t}=e,n=`<a href="/projects/${t._id}"><li><div class="project-heading">\n          <h2>${t.title}</h2>\n          <span class="status-indicator"></span>\n          <p>0 Issues</p>\n        </div>\n        <p>-By ${t.author}</p>\n        <p>${t.desc}</p></li></a>`;$(".projects-container > ul").prepend(n),$("#add-project-form").each((function(){this.reset()}))},error:function(e){console.log(e)}}),t.addClass("hide"),s.hide()}))}();